import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getDatabase, ref, get } from 'firebase/database';

export default function BuscarProyectoPorNombre() {
    const { usuarioID } = useParams();
    const [projects, setProjects] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResults, setSearchResults] = useState([]);

    useEffect(() => {
        const fetchProjects = async () => {
            try {
                const db = getDatabase();
                const projectsRef = ref(db, 'proyectos');
                const snapshot = await get(projectsRef);
                if (snapshot.exists()) {
                    const projectData = snapshot.val();
                    const projectList = Object.values(projectData);
                    setProjects(projectList);
                } else {
                    console.log('No projects found.');
                }
            } catch (error) {
                console.error('Error fetching projects:', error);
            }
        };

        fetchProjects();
    }, []);

    useEffect(() => {
        const results = projects.filter(project =>
            project.nombre.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setSearchResults(results);
    }, [searchTerm, projects]);

    const handleSearch = event => {
        setSearchTerm(event.target.value);
    };

    return (
        <div>
            <h2>Buscar Proyecto por Nombre</h2>
            <input
                type="text"
                placeholder="Nombre del proyecto"
                value={searchTerm}
                onChange={handleSearch}
            />
            <ul>
                {searchResults.map(project => (
                    <li key={project.id}>
                        <h3>{project.nombre}</h3>
                        <p>Fecha de Deadline: {project.fecha}</p>
                        {/* Add more project info to display */}
                    </li>
                ))}
            </ul>
        </div>
    );
}
